$_MODULE['<{advancedvatmanager}prestashop>advancedvatmanager_5c5c48e6b16c9c8e7d5f2924dba18076'] = 'Denne mulighed aktiverer eller deaktiverer for at tillade forskellige kunderegistreringer med samme momsnumre.';
