$_MODULE['<{advancedvatmanager}prestashop>advancedvatmanager_8a3ad48e7c2d0aa8fb38c2c03d13224a'] = 'Il modulo ha creato un proprio stato denominato \"Partita IVA convalidata\". Ma puoi usare qualsiasi stato personalizzato.';
