$_MODULE['<{advancedvatmanager}prestashop>advancedvatmanager_c0e292ad508997ec1c2a15d772cb128f'] = 'Ta opcja działa tylko w recepcji. Pole firmy będzie wyświetlane w formularzu adresowym, ale w każdym przypadku w backoffice.';
