CREATE TABLE IF NOT EXISTS `PREFIX_price_modification` ( id INT NOT NULL AUTO_INCREMENT, name_supplier VARCHAR(250) NOT NULL, id_store_product VARCHAR(10), file_supplier VARCHAR(50) NOT NULL, supplier_data JSON, formula VARCHAR(255), increment_formula VARCHAR(255), selected_supplier_price VARCHAR(50), active INT NOT NULL DEFAULT '0', old_supplier_price VARCHAR(50) NOT NULL DEFAULT '0', old_store_price VARCHAR(750) NOT NULL DEFAULT '[]', old_price_update DATETIME, updated_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (id) ) DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci ENGINE = InnoDB;
